import {useState, useEffect} from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';

function VideoSlide(){
    useEffect(function(){
        let slideStart = 0;
        let slideNum = 5;
        let dataId ='jobfind';
        let imgSrc = process.env.PUBLIC_URL+'/images/video/thumbnail/video_thumbnail';
        let prev= document.querySelector('.slideWrap .prev');
        let next= document.querySelector('.slideWrap .next');
        let slideArea = document.querySelector('.slideArea');
        let slide = slideArea.querySelectorAll('.slide');
        let child = slide[0];

        slideArea.appendChild(child);


        let startmouseX;
        let curmouseX;
        let mouse = false;
        let num;
        let originTranslateX;


    },[])
    return(
        <section className="VideoSlide">
            <div className="maintab">
                <h1 className='title'>
                    취업 교육 동영상
                </h1>
                <div className="select">
                    <button className="onepoint on"></button>
                    <button className="chippo"></button>
                </div>
            </div>
            <div className="subtab">
                <button className='subBtn on' data-id="prepare">취업준비</button>
                <button className='subBtn' data-id="resume">서류전형</button>
                <button className='subBtn' data-id="test">필기시험</button>
                <button className='subBtn' data-id="interview">면접전형</button>
            </div>
            <div className="slideWrap">
            <Swiper
                spaceBetween={50}
                slidesPerView={3}
                onSlideChange={() => console.log('slide change')}
                onSwiper={(swiper) => console.log(swiper)}
                >
                <SwiperSlide>Slide 1</SwiperSlide>
                <SwiperSlide>Slide 2</SwiperSlide>
                <SwiperSlide>Slide 3</SwiperSlide>
                <SwiperSlide>Slide 4</SwiperSlide>
                <SwiperSlide>Slide 4</SwiperSlide>
            </Swiper>
            </div>
        </section>
    )
}

export{VideoSlide}
